import React, {useState} from "react";
import WeatherInfo from "./WeatherInfo";
import axios from "axios";
import "./Weather.css";

export default function Weather(props) {
    const [weatherData, setWeatherData] = useState({ready: false});
    const [city, setCity] = useState(props.defaultCity);

    function handleResponse(response) {
        setWeatherData({
            ready: true,
            coordinates: response.data.coordinates,
            temperature: response.data.temperature.current,
            humidity: response.data.temperature.humidity,
            date: new Date(response.data.time * 1000),
            description: response.data.condition.description,
            icon: response.data.condition.icon,
            wind: response.data.wind.speed,
            city: response.data.city,
        });
    }

    function handleSubmit(event) {
        event.preventDefault();
        search();
    }

    function handleCityChange(event) {
        setCity(event.target.value);
    }

    function search() {
        const apiKey = "eac360db5fc86ft86450f3693e73o43f";
        let apiUrl = `https://api.shecodes.io/weather/v1/current?query=${city}&key=${apiKey}&units=metric`;

        axios.get(apiUrl).then(handleResponse);
    }

    if (weatherData.ready) {
        return (
            <div className="Weather">
                <a
                    href="https://www.shecodes.io/"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    <img src="/images/logo.png" className="logo" alt="SheCodes Logo"/>
                </a>
                <form onSubmit={handleSubmit}>
                    <div className="row">
                        <div className="col-9 ">
                            <input
                                type="search"
                                placeholder="Enter a city.."
                                className="form-control search-input"
                                onChange={handleCityChange}
                            />
                        </div>
                        <div className="col-3 p-0">
                            <input
                                type="submit"
                                value="Search"
                                className="btn btn-primary w-100"
                            />
                        </div>
                    </div>
                </form>
                <WeatherInfo data={weatherData}/>
                <footer>
                    This project was coded by{" "}
                    <a
                        href="https://deluxe-sundae-cbfa48.netlify.app"
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        Nataliia Matvieiko
                    </a>{" "}
                    and is{" "}
                    <a
                        href="https://github.com/Nataliia-Matvieiko/React-Weather-Search-Engine"
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        open-sourced on GitHub
                    </a>{" "}
                    and{" "}
                    <a
                        href="https://app.netlify.com/sites/super-salmiakki-c7f353/overview"
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        hosted on Netlify
                    </a>
                </footer>
            </div>
        );
    } else {
        search();
        return "Loading...";
    }
}
